export default (function (target) {
  return function (options) {
    var styles = createStyles(options.styles);
    if (styles) {
      target.appendChild(styles);
    }

    return options;
  };
});

function createStyles(styles) {
  if (!styles) {
    return null;
  }
  var s = document.createElement('style');
  s.textContent = styles;

  return s;
}