export default (function (original) {
  var oAttr = original.observedAttributes;

  if (oAttr) {
    oAttr.forEach(function (name) {
      Object.defineProperty(original.prototype, name, {
        get: function get() {
          return this['_' + name];
        },
        set: function set(value) {
          this.setAttribute(name, value);
        },
        configurable: true
      });
    });
  }

  return original;
});